// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const seatConfig = [
    { row: 'A', price: 150 },
    { row: 'B', price: 150 },
    { row: 'C', price: 250 },
    { row: 'D', price: 250 },
    { row: 'E', price: 250 },
    { row: 'F', price: 250 },
    { row: 'G', price: 250 },
  ];

  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSeatClick = (row, number, price) => {
    const seatId = `${row}${number}`;
    const isSelected = selectedSeats.find(seat => seat.id === seatId);

    if (isSelected) {
      setSelectedSeats(prev => prev.filter(seat => seat.id !== seatId));
    } else {
      setSelectedSeats(prev => [...prev, { id: seatId, price }]);
    }
  };

  const total = selectedSeats.reduce((sum, seat) => sum + seat.price, 0);

  const handlePayment = () => {
    if (!selectedSeats.length) return alert("Please select seats.");
    navigate('/payment', {
      state: {
        title: event.title,
        price: total,
        quantity: selectedSeats.length,
        selectedSeats,
        userDetails: { email: '' } // set properly if needed
      }
    });
  };

  if (!event) {
    return <div className="error-message">Event data missing. Please go back.</div>;
  }

  return (
    <div className="seat-page">
      <h2>Select Seats for {event.title}</h2>

      <div className="seat-container">
        {seatConfig.map(({ row, price }) => (
          <div className="seat-row" key={row}>
            <span className="row-label">{row}</span>
            {Array.from({ length: 14 }).map((_, i) => {
              const seatId = `${row}${i + 1}`;
              const isSelected = selectedSeats.find(seat => seat.id === seatId);
              return (
                <div
                  key={seatId}
                  className={`seat ${isSelected ? 'selected' : ''}`}
                  onClick={() => handleSeatClick(row, i + 1, price)}
                >
                  {i + 1}
                </div>
              );
            })}
            <span className="seat-price">₹{price}</span>
          </div>
        ))}
      </div>

      <div className="screen-label">All eyes this way please!</div>

      <div className="summary">
        <div>Selected Seats: {selectedSeats.map(seat => seat.id).join(', ') || 'None'}</div>
        <div>Total: ₹{total}</div>
        <button className="pay-btn" onClick={handlePayment}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
