import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const seatTiers = {
  Silver: { price: 200, seats: 20 },
  Gold: { price: 300, seats: 10 },
  Platinum: { price: 400, seats: 6 }
};

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return <div className="seat-selection-page">Event data missing. Please go back.</div>;
  }

  const toggleSeat = (seat, tier) => {
    const seatId = `${tier}-${seat}`;
    setSelectedSeats(prev =>
      prev.includes(seatId)
        ? prev.filter(s => s !== seatId)
        : [...prev, seatId]
    );
  };

  const getTotal = () => {
    return selectedSeats.reduce((total, seat) => {
      const tier = seat.split('-')[0];
      return total + seatTiers[tier].price;
    }, 0);
  };

  const handleProceed = () => {
    navigate('/payment', {
      state: {
        title: event.title,
        quantity: selectedSeats.length,
        selectedSeats,
        price: getTotal()
      }
    });
  };

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>

      {Object.entries(seatTiers).map(([tier, { price, seats }]) => (
        <div key={tier} className="tier-section">
          <h3>{tier} (₹{price})</h3>
          <div className="seats-row">
            {[...Array(seats).keys()].map(i => {
              const seatLabel = `${tier}-${i + 1}`;
              const isSelected = selectedSeats.includes(seatLabel);
              return (
                <div
                  key={seatLabel}
                  className={`seat ${isSelected ? 'selected' : ''}`}
                  onClick={() => toggleSeat(i + 1, tier)}
                >
                  {i + 1}
                </div>
              );
            })}
          </div>
        </div>
      ))}

      <div className="selection-summary">
        <p>Selected Seats: {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None'}</p>
        <p>Total: ₹{getTotal()}</p>
        <button
          className="proceed-btn"
          disabled={selectedSeats.length === 0}
          onClick={handleProceed}
        >
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
