// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: 'Mumbai',
    category: 'all',
    priceRange: [0, 5000]
  });

  // Mock API data
  useEffect(() => {
    const mockEvents = [
      {
        id: "coldplay-2025",
        type: "concert",
        title: "Coldplay: Music of the Spheres World Tour",
        date: "2025-04-15",
        time: "7:30 PM",
        price: 3500,
        location: "Mumbai",
        language: "English",
        poster: "https://source.unsplash.com/random/800x600?concert,coldplay",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola", "Visa"],
          duration: "2h 30m",
          venue: "DY Patil Stadium"
        }
      },
      {
        id: "avengers-endgame",
        type: "movie",
        title: "Avengers: Endgame",
        date: "2025-05-01",
        time: "6:00 PM",
        price: 300,
        location: "Mumbai",
        language: "English",
        poster: "https://source.unsplash.com/random/800x600?movie,avengers",
        details: {
          cast: ["Robert Downey Jr.", "Chris Evans", "Scarlett Johansson"],
          director: "Anthony & Joe Russo",
          duration: "3h 1m",
          theater: "PVR Cinemas, Phoenix Mall"
        }
      }
    ];
    setEvents(mockEvents);
  }, []);

  const filteredEvents = events.filter(event => {
    return (
      (filters.category === 'all' || event.type === filters.category) &&
      (filters.location === '' || event.location === filters.location) &&
      event.price >= filters.priceRange[0] &&
      event.price <= filters.priceRange[1]
    );
  });

  return (
    <div className="events-page">
      <Header />

      <div className="filters-container">
        <div className="filter-group">
          <label>Date</label>
          <input
            type="date"
            value={filters.date}
            onChange={(e) => setFilters({...filters, date: e.target.value})}
          />
        </div>

        <div className="filter-group">
          <label>Location</label>
          <select
            value={filters.location}
            onChange={(e) => setFilters({...filters, location: e.target.value})}
          >
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
            <option value="Bengaluru">Bengaluru</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Category</label>
          <select
            value={filters.category}
            onChange={(e) => setFilters({...filters, category: e.target.value})}
          >
            <option value="all">All Events</option>
            <option value="concert">Concerts</option>
            <option value="movie">Movies</option>
          </select>
        </div>
      </div>

      <div className="events-grid">
        {filteredEvents.map(event => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
