import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const SeatSelectionPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const event = location.state;

  const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']; // A near screen
  const cols = 14;
  const [selectedSeats, setSelectedSeats] = useState([]);

  const getSeatPrice = (row) => {
    return ['A', 'B'].includes(row) ? 150 : 250;
  };

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const handleProceed = () => {
    if (selectedSeats.length === 0) {
      alert("Please select seats first.");
      return;
    }

    const total = selectedSeats.reduce((sum, seat) => {
      const price = getSeatPrice(seat[0]);
      return sum + price;
    }, 0);

    navigate('/payment', {
      state: {
        ...event,
        selectedSeats,
        quantity: selectedSeats.length,
        total
      }
    });
  };

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event?.title || "Movie"}</h2>

      <div className="screen">SCREEN THIS WAY</div>

      <div className="seats-container">
        {rows.map((row, rowIdx) => (
          <div className="seat-row" key={row}>
            {[...Array(cols)].map((_, colIdx) => {
              const seatLabel = `${row}${colIdx + 1}`;
              const isAisle = colIdx === 4 || colIdx === 9;
              const isSelected = selectedSeats.includes(seatLabel);
              const seatPrice = getSeatPrice(row);

              return (
                <div
                  key={seatLabel}
                  className={`seat ${isSelected ? 'selected' : ''} ${isAisle ? 'aisle' : ''}`}
                  onClick={() => toggleSeat(seatLabel)}
                >
                  {seatLabel}
                  <span className="price-tag">₹{seatPrice}</span>
                </div>
              );
            })}
          </div>
        ))}
      </div>

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>
          Total Price: ₹
          {selectedSeats.reduce((sum, s) => sum + getSeatPrice(s[0]), 0)}
        </p>
        <button onClick={handleProceed} className="proceed-btn">
          Continue to Payment ({selectedSeats.length} seat{selectedSeats.length !== 1 ? 's' : ''})
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
