// src/pages/ConcertSeatSelectionPage.js
import React, { useState } from 'react';
import './ConcertSeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const ConcertSeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const zones = [
    { name: 'Platinum', price: 500, rows: 2, cols: 10 },
    { name: 'Gold', price: 300, rows: 3, cols: 12 },
    { name: 'Silver', price: 150, rows: 4, cols: 15 },
  ];

  const toggleSeat = (seatId) => {
    setSelectedSeats(prev =>
      prev.includes(seatId) ? prev.filter(s => s !== seatId) : [...prev, seatId]
    );
  };

  const getSeatPrice = (seatId) => {
    for (const zone of zones) {
      if (seatId.startsWith(zone.name)) return zone.price;
    }
    return 0;
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat");
      return;
    }
    const avgPrice = selectedSeats.reduce((sum, s) => sum + getSeatPrice(s), 0) / selectedSeats.length;

    navigate('/payment', {
      state: {
        title: event.title,
        quantity: selectedSeats.length,
        price: avgPrice,
        selectedSeats
      }
    });
  };

  return (
    <div className="concert-container">
      <h2>Select Seats for {event?.title || "Concert"}</h2>
      <div className="stage">STAGE</div>

      {zones.map(zone => (
        <div key={zone.name} className={`zone ${zone.name.toLowerCase()}`}>
          <h3>{zone.name} (₹{zone.price})</h3>
          <div className="zone-grid">
            {Array.from({ length: zone.rows }).map((_, rowIndex) => (
              <div key={rowIndex} className="zone-row">
                {Array.from({ length: zone.cols }).map((_, colIndex) => {
                  const seatId = `${zone.name}${rowIndex + 1}-${colIndex + 1}`;
                  return (
                    <div
                      key={seatId}
                      className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                      onClick={() => toggleSeat(seatId)}
                    >
                      {seatId.split('-')[1]}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        </div>
      ))}

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total Price: ₹{selectedSeats.reduce((sum, seat) => sum + getSeatPrice(seat), 0)}</p>
        <button className="pay-btn" onClick={handlePayment}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default ConcertSeatSelectionPage;
