// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    date: '',
    location: 'Mumbai',
    category: 'all',
    priceRange: [0, 5000],
  });
  const [showFilters, setShowFilters] = useState(false);

  const transformEvent = (backendEvent) => {
    let type = 'other';
    if (backendEvent.type === 'MovieSchema') type = 'movie';
    else if (backendEvent.type === 'ConcertSchema') type = 'concert';
    else if (backendEvent.type === 'TheatreSchema' || backendEvent.type === 'Theatre') type = 'theatre';

    return {
      id: backendEvent.eventId,
      title: backendEvent.name,
      date: backendEvent.eventDate,
      poster: backendEvent.posterImage || 'https://via.placeholder.com/300x400?text=No+Image',
      type,
      price: 0,
      location: 'Mumbai',
    };
  };

  const fetchEvents = () => {
    const query = new URLSearchParams();
    if (filters.date) query.append('date', filters.date);
    if (filters.location) query.append('location', filters.location);
    if (filters.category !== 'all') query.append('category', filters.category);

    fetch(`http://localhost:3000/api/events`)
      .then((res) => res.json())
      .then((data) => {
        const transformed = data.events.map(transformEvent);
        setEvents(transformed);
      })
      .catch((err) => console.error('Failed to fetch:', err));
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  const filteredEvents = events.filter((event) => {
    const matchDate = !filters.date || new Date(event.date).toISOString().slice(0, 10) === filters.date;
    const matchLocation = event.location === filters.location;
    const matchCategory = filters.category === 'all' || event.type === filters.category;
    const matchPrice = event.price >= filters.priceRange[0] && event.price <= filters.priceRange[1];
    return matchDate && matchLocation && matchCategory && matchPrice;
  });

  return (
    <div className="events-page">
      <Header />

      <div className="filter-toggle">
        <button onClick={() => setShowFilters(!showFilters)}>
          {showFilters ? 'Hide Filters' : 'Show Filters'}
        </button>
      </div>

      {showFilters && (
        <div className="filters-panel">
          <div className="filter-group">
            <label>Date</label>
            <input
              type="date"
              value={filters.date}
              onChange={(e) => setFilters({ ...filters, date: e.target.value })}
            />
          </div>
          <div className="filter-group">
            <label>Location</label>
            <select
              value={filters.location}
              onChange={(e) => setFilters({ ...filters, location: e.target.value })}
            >
              <option value="Mumbai">Mumbai</option>
              <option value="Delhi">Delhi</option>
              <option value="Bengaluru">Bengaluru</option>
            </select>
          </div>
          <div className="filter-group">
            <label>Category</label>
            <select
              value={filters.category}
              onChange={(e) => setFilters({ ...filters, category: e.target.value })}
            >
              <option value="all">All</option>
              <option value="movie">Movies</option>
              <option value="concert">Concerts</option>
              <option value="theatre">Theatre</option>
            </select>
          </div>
          <div className="filter-group">
            <label>Price Range</label>
            <input
              type="range"
              min="0"
              max="5000"
              value={filters.priceRange[1]}
              onChange={(e) => setFilters({ ...filters, priceRange: [0, parseInt(e.target.value)] })}
            />
            <div className="price-range">₹0 - ₹{filters.priceRange[1]}</div>
          </div>
        </div>
      )}

      <div className="events-grid">
        {filteredEvents.length > 0 ? (
          filteredEvents.map((event) => <EventCard key={event.id} event={event} />)
        ) : (
          <div className="no-events">No events found.</div>
        )}
      </div>
    </div>
  );
};

export default EventsPage;
