// src/pages/EventsPage.js
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import EventCard from '../components/EventCard';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    category: 'all',
    date: '',
    location: '',
    language: 'all',
    priceRange: [0, 5000]
  });

  // Mock data with additional fields
  useEffect(() => {
    const mockEvents = [
      {
        id: 1,
        title: "Coldplay Concert",
        type: "concert",
        date: "2025-06-15",
        price: 5000,
        location: "Mumbai",
        language: "English",
        genre: "Rock",
        venue: "DY Patil Stadium",
        poster: "https://source.unsplash.com/random/800x600?concert",
        details: {
          artists: ["Coldplay"],
          sponsors: ["Coca-Cola"],
          duration: "2h 30m"
        }
      },
      {
        id: 2,
        title: "Avengers: Endgame",
        type: "movie",
        date: "2025-05-01",
        price: 300,
        location: "Delhi",
        language: "Hindi",
        genre: "Action",
        venue: "PVR Cinemas",
        poster: "https://source.unsplash.com/random/800x600?movie",
        details: {
          cast: ["Robert Downey Jr.", "Chris Evans"],
          director: "Russo Brothers",
          duration: "3h 1m"
        }
      }
    ];
    setEvents(mockEvents);
  }, []);

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const filteredEvents = events.filter(event => {
    return (
      (filters.category === 'all' || event.type === filters.category) &&
      (filters.location === '' || event.location === filters.location) &&
      (filters.language === 'all' || event.language === filters.language) &&
      event.price >= filters.priceRange[0] &&
      event.price <= filters.priceRange[1]
    );
  });

  return (
    <div className="events-page">
      <Header />

      <div className="filters-sidebar">
        <div className="filter-group">
          <h3>Date</h3>
          <input 
            type="date" 
            onChange={(e) => handleFilterChange('date', e.target.value)}
          />
        </div>

        <div className="filter-group">
          <h3>Location</h3>
          <select onChange={(e) => handleFilterChange('location', e.target.value)}>
            <option value="">All Locations</option>
            <option value="Mumbai">Mumbai</option>
            <option value="Delhi">Delhi</option>
          </select>
        </div>

        <div className="filter-group">
          <h3>Category</h3>
          <select onChange={(e) => handleFilterChange('category', e.target.value)}>
            <option value="all">All Events</option>
            <option value="concert">Concerts</option>
            <option value="movie">Movies</option>
          </select>
        </div>

        <div className="filter-group">
          <h3>Language</h3>
          <select onChange={(e) => handleFilterChange('language', e.target.value)}>
            <option value="all">All Languages</option>
            <option value="English">English</option>
            <option value="Hindi">Hindi</option>
          </select>
        </div>

        <div className="filter-group">
          <h3>Price Range</h3>
          <div className="price-range">
            <input
              type="range"
              min="0"
              max="5000"
              value={filters.priceRange[1]}
              onChange={(e) => handleFilterChange('priceRange', [0, e.target.value])}
            />
            <span>₹0 - ₹{filters.priceRange[1]}</span>
          </div>
        </div>
      </div>

      <div className="events-grid">
        {filteredEvents.map(event => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
