// src/pages/SeatSelectionPageConcert.js
import React, { useState } from 'react';
import './SeatSelectionPageConcert.css';
import { useLocation, useNavigate } from 'react-router-dom';

const SeatSelectionPageConcert = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { event } = location.state || {};
  const [selectedSeats, setSelectedSeats] = useState([]);

  const tiers = [
    { name: 'Platinum', price: 600, rows: ['A', 'B', 'C'], color: 'platinum' },
    { name: 'Gold', price: 400, rows: ['D', 'E', 'F'], color: 'gold' },
    { name: 'Silver', price: 250, rows: ['G', 'H', 'I'], color: 'silver' },
  ];

  const seatsPerRow = 20;

  const toggleSeat = (seatId) => {
    setSelectedSeats((prev) =>
      prev.includes(seatId) ? prev.filter((s) => s !== seatId) : [...prev, seatId]
    );
  };

  const getPrice = (seatId) => {
    const row = seatId[0];
    for (const tier of tiers) {
      if (tier.rows.includes(row)) return tier.price;
    }
    return 0;
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    const avgPrice = selectedSeats.reduce((sum, s) => sum + getPrice(s), 0) / selectedSeats.length;

    navigate('/payment', {
      state: {
        title: event?.title || 'Concert Event',
        quantity: selectedSeats.length,
        price: avgPrice,
        selectedSeats,
      },
    });
  };

  return (
    <div className="concert-seat-container">
      <h2>Select Seats for {event?.title || 'Concert'}</h2>

      <div className="stage">STAGE</div>

      {tiers.map((tier, i) => (
        <div key={tier.name} className={`tier-section ${tier.color}`}>
          <h3>{tier.name} (₹{tier.price})</h3>
          {tier.rows.map((row) => (
            <div className="seat-row" key={row}>
              <div className="row-label">{row}</div>
              <div className="seats">
                {Array.from({ length: seatsPerRow }, (_, idx) => {
                  const seatId = `${row}${idx + 1}`;
                  return (
                    <div
                      key={seatId}
                      className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                      onClick={() => toggleSeat(seatId)}
                    >
                      {seatId}
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>
      ))}

      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: ₹{selectedSeats.reduce((sum, seat) => sum + getPrice(seat), 0)}</p>
        <button onClick={handlePayment} className="pay-btn">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPageConcert;
