import React, { useState, useEffect } from 'react';

function EventsPage() {
  // State for filter drawer open/close
  const [openFilter, setOpenFilter] = useState(false);
  // Filter states
  const [selectedDate, setSelectedDate] = useState('');        // format: 'YYYY-MM-DD'
  const [selectedLocation, setSelectedLocation] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 1000 });
  // Events data and filtered results
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const eventsPerPage = 8;  // showing 8 events per page (can adjust up to 12 as needed)

  // Fetch all events on component mount (could also fetch per filter/page on server-side)
  useEffect(() => {
    // Example: Fetch events from API (without filters here for initial load)
    fetch('/api/events')
      .then(res => res.json())
      .then(data => {
        setEvents(data);
        setFilteredEvents(data);  // initialize filtered list with all events
      })
      .catch(err => console.error('Failed to fetch events', err));
  }, []);

  // Apply filters whenever filter inputs or the events list change
  useEffect(() => {
    // Ideally, server-side filtering would be done here by calling an API with query params.
    // For example:
    // fetch(`/api/events?date=${selectedDate}&location=${selectedLocation}&category=${selectedCategory}&minPrice=${priceRange.min}&maxPrice=${priceRange.max}`)
    //   .then(res => res.json())
    //   .then(data => setFilteredEvents(data));
    //
    // For this demo, we filter the already-fetched events array on the client side:
    let updated = events;
    if (selectedDate) {
      updated = updated.filter(evt => {
        // Compare event date in 'YYYY-MM-DD' format (assumes evt.eventDate is ISO string or Date)
        const eventDateStr = (typeof evt.eventDate === 'string') 
          ? evt.eventDate.substring(0, 10) 
          : new Date(evt.eventDate).toISOString().substring(0, 10);
        return eventDateStr === selectedDate;
      });
    }
    if (selectedLocation) {
      updated = updated.filter(evt => evt.location === selectedLocation);
    }
    if (selectedCategory) {
      updated = updated.filter(evt => evt.category === selectedCategory);
    }
    if (priceRange) {
      updated = updated.filter(evt => {
        const price = evt.price || 0;
        return price >= priceRange.min && price <= priceRange.max;
      });
    }
    setFilteredEvents(updated);
    setCurrentPage(1);  // Reset to first page whenever filters change
  }, [events, selectedDate, selectedLocation, selectedCategory, priceRange]);

  // Determine events to display on the current page
  const indexOfLast = currentPage * eventsPerPage;
  const indexOfFirst = indexOfLast - eventsPerPage;
  const currentEvents = filteredEvents.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);

  const goToPage = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  return (
    <div className="events-page">
      {/* Filter Side Drawer */}
      <div className={`filter-drawer ${openFilter ? 'open' : ''}`}>
        <div className="filter-header">
          <h2>Filters</h2>
          <button className="close-btn" onClick={() => setOpenFilter(false)}>Ã—</button>
        </div>
        <div className="filter-section">
          <label htmlFor="dateFilter">Date</label>
          <input 
            id="dateFilter"
            type="date" 
            value={selectedDate} 
            onChange={(e) => setSelectedDate(e.target.value)} 
          />
        </div>
        <div className="filter-section">
          <label htmlFor="locationFilter">Location</label>
          <select 
            id="locationFilter"
            value={selectedLocation} 
            onChange={(e) => setSelectedLocation(e.target.value)}
          >
            <option value="">All</option>
            <option value="New York">New York</option>
            <option value="Chicago">Chicago</option>
            {/* Other locations can be added here or generated dynamically */}
          </select>
        </div>
        <div className="filter-section">
          <label htmlFor="categoryFilter">Category</label>
          <select 
            id="categoryFilter"
            value={selectedCategory} 
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="">All</option>
            <option value="Concert">Concert</option>
            <option value="Workshop">Workshop</option>
            {/* Other categories as needed */}
          </select>
        </div>
        <div className="filter-section">
          <label>Price Range</label>
          <div className="price-inputs">
            <input 
              type="number" 
              placeholder="Min" 
              value={priceRange.min} 
              onChange={(e) => setPriceRange({
                ...priceRange, min: Number(e.target.value) || 0
              })}
            />
            <input 
              type="number" 
              placeholder="Max" 
              value={priceRange.max} 
              onChange={(e) => setPriceRange({
                ...priceRange, max: Number(e.target.value) || 0
              })}
            />
          </div>
        </div>
        <button className="apply-filters btn" onClick={() => setOpenFilter(false)}>
          Apply
        </button>
      </div>

      {/* Backdrop overlay (shown on mobile when filter drawer is open) */}
      {openFilter && <div className="backdrop" onClick={() => setOpenFilter(false)}></div>}

      {/* Main Content Area */}
      <div className="events-content">
        <div className="events-header">
          <h1>Events</h1>
          <button className="open-filters-btn btn" onClick={() => setOpenFilter(prev => !prev)}>
            Filters
          </button>
        </div>

        {/* Events Grid */}
        <div className="events-grid">
          {currentEvents.map(evt => (
            <div key={evt.id} className="event-card">
              <h3>{evt.title}</h3>
              <p className="event-date">
                {new Date(evt.eventDate).toLocaleDateString()} {/* formatted date */}
              </p>
              <p className="event-location">{evt.location}</p>
              <p className="event-category">{evt.category}</p>
              <p className="event-price">
                {evt.price ? `$${evt.price}` : 'Free'}
              </p>
            </div>
          ))}
          {currentEvents.length === 0 && (
            <p className="no-events-msg">No events found for the selected filters.</p>
          )}
        </div>

        {/* Pagination Controls */}
        <div className="pagination">
          <button 
            onClick={() => goToPage(currentPage - 1)} 
            disabled={currentPage === 1}
            className="btn"
          >
            Previous
          </button>
          <span className="page-info">Page {currentPage} of {totalPages || 1}</span>
          <button 
            onClick={() => goToPage(currentPage + 1)} 
            disabled={currentPage === totalPages || totalPages === 0}
            className="btn"
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}

export default EventsPage;
