// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  if (!event) {
    return <div style={{ padding: '2rem', textAlign: 'center' }}>Event data missing. Please go back.</div>;
  }

  // Sample tiered seat layout
  const seatLayout = {
    "Silver": { seats: 20, price: event.price || 200 },
    "Gold": { seats: 10, price: (event.price || 200) + 100 },
    "Platinum": { seats: 6, price: (event.price || 200) + 200 }
  };

  const toggleSeat = (tier, index) => {
    const seatId = `${tier}-${index + 1}`;
    setSelectedSeats(prev =>
      prev.includes(seatId) ? prev.filter(s => s !== seatId) : [...prev, seatId]
    );
  };

  const getPriceForSeat = (seatId) => {
    const tier = seatId.split('-')[0];
    return seatLayout[tier].price;
  };

  const totalPrice = selectedSeats.reduce((acc, seatId) => acc + getPriceForSeat(seatId), 0);

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat.");
      return;
    }

    navigate('/payment', {
      state: {
        title: event.title,
        price: totalPrice / selectedSeats.length,
        quantity: selectedSeats.length,
        selectedSeats,
        userDetails: { email: '' } // fallback email; fill later
      }
    });
  };

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>

      {Object.entries(seatLayout).map(([tier, info]) => (
        <div key={tier} className="tier-section">
          <h3>{tier} (₹{info.price})</h3>
          <div className="seat-row">
            {Array.from({ length: info.seats }, (_, i) => {
              const seatId = `${tier}-${i + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''}`}
                  onClick={() => toggleSeat(tier, i)}
                >
                  {seatId}
                </div>
              );
            })}
          </div>
        </div>
      ))}

      <div className="selection-summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: ₹{totalPrice}</p>
        <button onClick={handlePayment} className="pay-button">
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
