import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './EventsPage.css';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  // Mock data for events
  useEffect(() => {
    const mockEvents = [
      {
        id: "1",
        name: "Summer Music Festival",
        date: "2025-04-15",
        time: "6:00 PM",
        location: "Central Park, New York",
        image: "https://images.unsplash.com/photo-1529156069898-49953e39b3ac",
        description: "Join us for an unforgettable night of live music.",
        category: "Music Shows"
      },
      {
        id: "2",
        name: "Winter Wonderland Concert",
        date: "2025-12-20",
        time: "7:30 PM",
        location: "Madison Square Garden, New York",
        image: "https://images.unsplash.com/photo-1515169067865-dae1abe0b6b3",
        description: "Experience the magic of winter with live music.",
        category: "Music Shows"
      },
      {
        id: "3",
        name: "Comedy Night Extravaganza",
        date: "2025-05-10",
        time: "8:00 PM",
        location: "Comedy Club, Los Angeles",
        image: "https://images.unsplash.com/photo-1519671482749-fd09be7ccebf",
        description: "Laugh out loud with the best comedians in town.",
        category: "Comedy Shows"
      },
      {
        id: "4",
        name: "Theatre Workshop",
        date: "2025-06-25",
        time: "10:00 AM",
        location: "Downtown Theatre, Chicago",
        image: "https://images.unsplash.com/photo-1559628232-ded576a4adbc",
        description: "Learn acting techniques from industry professionals.",
        category: "Workshops"
      }
    ];
    setEvents(mockEvents);
  }, []);

  // Filter events based on selected date and category
  const filteredEvents = events.filter(event => {
    let matchesDate = true;
    let matchesCategory = true;

    if (selectedDate) {
      matchesDate = event.date.includes(selectedDate);
    }

    if (selectedCategory) {
      matchesCategory = event.category === selectedCategory;
    }

    return matchesDate && matchesCategory;
  });

  return (
    <div className="events-page">
      {/* Header with search bar */}
      <div className="header">
        <div className="search-bar">
          <input type="text" placeholder="Search for Events, Plays, Sports and Activities" />
        </div>
      </div>

      {/* Background section */}
      <div className="background-image">
        <h1>Events In Your City</h1>
      </div>

      {/* Category filters (horizontal) */}
      <div className="category-filters">
        <button 
          className={selectedCategory === '' ? 'active' : ''} 
          onClick={() => setSelectedCategory('')}
        >
          All
        </button>
        <button 
          className={selectedCategory === 'Workshops' ? 'active' : ''} 
          onClick={() => setSelectedCategory('Workshops')}
        >
          Workshops
        </button>
        <button 
          className={selectedCategory === 'Music Shows' ? 'active' : ''} 
          onClick={() => setSelectedCategory('Music Shows')}
        >
          Music Shows
        </button>
        <button 
          className={selectedCategory === 'Comedy Shows' ? 'active' : ''} 
          onClick={() => setSelectedCategory('Comedy Shows')}
        >
          Comedy Shows
        </button>
      </div>

      <div className="main-content">
        {/* Left sidebar with filters */}
        <div className="filters-sidebar">
          <h3>Filters</h3>

          <div className="filter-section">
            <h4>Date</h4>
            <div className="date-options">
              <button className={selectedDate === '' ? 'active' : ''} onClick={() => setSelectedDate('')}>All Dates</button>
              <button className={selectedDate === 'Today' ? 'active' : ''} onClick={() => setSelectedDate('Today')}>Today</button>
              <button className={selectedDate === 'Tomorrow' ? 'active' : ''} onClick={() => setSelectedDate('Tomorrow')}>Tomorrow</button>
              <button className={selectedDate === 'Weekend' ? 'active' : ''} onClick={() => setSelectedDate('Weekend')}>This Weekend</button>
            </div>
          </div>

          <div className="filter-section">
            <h4>Price</h4>
            <div className="price-options">
              <label><input type="checkbox" /> Free</label>
              <label><input type="checkbox" /> 0-500</label>
              <label><input type="checkbox" /> 501-2000</label>
              <label><input type="checkbox" /> Above 2000</label>
            </div>
          </div>

          <button className="browse-venues">Browse by Venues</button>
        </div>

        {/* Events grid */}
        <div className="events-container">
          <div className="events-grid">
            {filteredEvents.map(event => (
              <Link to={`/events/${event.id}`} key={event.id} className="event-card">
                <div className="event-image-container">
                  <img src={event.image} alt={event.name} className="event-image" />
                </div>
                <div className="event-info">
                  <h3>{event.name}</h3>
                  <p className="event-date">{new Date(event.date).toLocaleDateString()}</p>
                  <p className="event-location">{event.location}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
