import React from 'react';
import { useLocation } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const location = useLocation();
  const { title, price, quantity } = location.state || {};

  // Calculate total price
  const subtotal = price * quantity;
  const tax = subtotal * 0.05; // Example tax rate: 5%
  const total = subtotal + tax;

  return (
    <div className="payment-container">
      <h1>Checkout</h1>

      {/* Order Summary */}
      <section className="order-summary">
        <h2>Order Summary</h2>
        <div className="product-details">
          <div className="product-item">
            <img src="https://via.placeholder.com/60" alt="Event Poster" />
            <div className="product-info">
              <h3>{title}</h3>
              <p>Quantity: {quantity}</p>
            </div>
            <div className="product-price">₹{subtotal}</div>
          </div>
        </div>
        <div className="total-section">
          <div className="total-row">
            <span>Subtotal</span>
            <span>₹{subtotal}</span>
          </div>
          <div className="total-row">
            <span>Tax (5%)</span>
            <span>₹{tax.toFixed(2)}</span>
          </div>
          <div className="total-row final">
            <span>Total</span>
            <span>₹{total.toFixed(2)}</span>
          </div>
        </div>
      </section>

      {/* Payment Form */}
      {/* Add your payment form here */}
    </div>
  );
};

export default PaymentPage;
