// src/pages/SeatSelectionPage.js
import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
const seatsPerRow = 14;

const tieredPricing = (row) => {
  if (['A', 'B'].includes(row)) return 150;
  return 300;
};

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeat = (seatId) => {
    setSelectedSeats((prev) =>
      prev.includes(seatId) ? prev.filter((s) => s !== seatId) : [...prev, seatId]
    );
  };

  const getTotal = () => {
    return selectedSeats.reduce((total, seat) => {
      const row = seat.charAt(0);
      return total + tieredPricing(row);
    }, 0);
  };

  const handleContinue = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat.");
      return;
    }
    navigate('/payment', {
      state: {
        title: event?.title || 'Selected Movie',
        selectedSeats,
        quantity: selectedSeats.length,
        price: getTotal(),
      },
    });
  };

  if (!event) return <div className="error">Event data missing. Please go back.</div>;

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>
      <div className="seat-map-wrapper">
        {rows.map((row, idx) => (
          <div className="seat-row" key={row}>
            {[...Array(seatsPerRow)].map((_, col) => {
              const seatId = `${row}${col + 1}`;
              const isSelected = selectedSeats.includes(seatId);
              const isLowerTier = ['A', 'B'].includes(row);
              return (
                <div
                  key={seatId}
                  className={`seat ${isSelected ? 'selected' : ''} ${isLowerTier ? 'low-tier' : ''}`}
                  onClick={() => toggleSeat(seatId)}
                >
                  {seatId}
                </div>
              );
            })}
            {(row === 'B' || row === 'E') && <div className="aisle-gap" />}
          </div>
        ))}
        <div className="screen-label">SCREEN THIS WAY</div>
        <div className="screen"></div>
      </div>
      <div className="summary-box">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: â‚¹{getTotal()}</p>
        <button className="continue-btn" onClick={handleContinue}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
