// SeatSelectionPage.js
import React, { useState } from 'react';
import './SeatSelectionPage.css';
import { useLocation, useNavigate } from 'react-router-dom';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state;

  const silverRows = ['A', 'B'];
  const goldRows = ['C', 'D', 'E', 'F'];
  const platinumRows = ['G', 'H', 'I', 'J'];

  const seatLayout = [...silverRows, ...goldRows, ...platinumRows];
  const seatsPerRow = 14;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const getPrice = (row) => {
    if (silverRows.includes(row)) return 200;
    if (goldRows.includes(row)) return 300;
    if (platinumRows.includes(row)) return 400;
    return 0;
  };

  const toggleSeat = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat)
        ? prev.filter((s) => s !== seat)
        : [...prev, seat]
    );
  };

  const calculateTotal = () => {
    return selectedSeats.reduce((total, seat) => {
      const row = seat.charAt(0);
      return total + getPrice(row);
    }, 0);
  };

  const handlePayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat.');
      return;
    }

    navigate('/payment', {
      state: {
        ...event,
        selectedSeats,
        quantity: selectedSeats.length,
        totalPrice: calculateTotal(),
      }
    });
  };

  return (
    <div className="seat-selection-container">
      <h2>Select Seats for {event?.title || "Movie"}</h2>
      <div className="seating-area">
        {seatLayout.map((row, rowIndex) => (
          <div
            key={row}
            className={`seat-row ${
              rowIndex === 1 ? 'bottom-gap' : rowIndex === 5 ? 'middle-gap' : ''
            }`}
          >
            {[...Array(seatsPerRow)].map((_, i) => {
              const seatId = `${row}${i + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : ''} ${silverRows.includes(row) ? 'silver' : goldRows.includes(row) ? 'gold' : 'platinum'}`}
                  onClick={() => toggleSeat(seatId)}
                >
                  {seatId}
                </div>
              );
            })}
          </div>
        ))}
      </div>
      <div className="screen">SCREEN</div>
      <div className="summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: â‚¹{calculateTotal()}</p>
        <button className="pay-button" onClick={handlePayment}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
