import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SeatSelectionPage.css';

const SeatSelectionPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const event = location.state?.event;

  const [selectedSeats, setSelectedSeats] = useState([]);

  const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
  const columns = 14;

  const toggleSeat = (row, number) => {
    const seatId = `${row}${number}`;
    setSelectedSeats(prev =>
      prev.includes(seatId)
        ? prev.filter(seat => seat !== seatId)
        : [...prev, seatId]
    );
  };

  const getSeatPrice = (row) => {
    return row === 'A' || row === 'B' ? 150 : 250;
  };

  const calculateTotal = () => {
    return selectedSeats.reduce((total, seat) => {
      const row = seat.charAt(0);
      return total + getSeatPrice(row);
    }, 0);
  };

  const handleProceed = () => {
    if (selectedSeats.length === 0) {
      alert("Please select at least one seat.");
      return;
    }

    navigate('/payment', {
      state: {
        ...event,
        selectedSeats,
        quantity: selectedSeats.length,
        price: calculateTotal()
      }
    });
  };

  if (!event) return <div className="error-message">Event data missing.</div>;

  return (
    <div className="seat-selection-page">
      <h2>Select Seats for {event.title}</h2>
      <div className="seat-legend">
        <div><span className="seat available"></span> Available</div>
        <div><span className="seat selected"></span> Selected</div>
        <div><span className="seat disabled"></span> Unavailable</div>
      </div>

      <div className="seat-map">
        {rows.map((row, rowIndex) => (
          <div key={row} className="seat-row">
            <span className="row-label">{row}</span>
            {[...Array(columns)].map((_, i) => {
              // Aisle between seat blocks
              const isAisle = i === 4 || i === 9;
              if (isAisle) return <span key={`aisle-${i}`} className="aisle-space"></span>;

              const seatId = `${row}${i + 1}`;
              return (
                <div
                  key={seatId}
                  className={`seat ${selectedSeats.includes(seatId) ? 'selected' : 'available'}`}
                  onClick={() => toggleSeat(row, i + 1)}
                >
                  {i + 1}
                </div>
              );
            })}
          </div>
        ))}
      </div>

      <div className="screen">Screen This Side</div>

      <div className="selection-summary">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total: â‚¹{calculateTotal()}</p>
        <button className="proceed-btn" onClick={handleProceed}>
          Continue to Payment ({selectedSeats.length} seats)
        </button>
      </div>
    </div>
  );
};

export default SeatSelectionPage;
