// src/pages/EventDetailsPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookingModal from '../components/BookingModal';
import './EventDetailsPage.css';

const EventDetailsPage = () => {
  const { eventId } = useParams();
  const [event, setEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/events/${eventId}`);
        const data = await response.json();
        setEvent(data.eventDetails || null);
      } catch (error) {
        console.error('Error fetching event details:', error);
      }
    };
    fetchEventDetails();
  }, [eventId]);

  if (!event) return <div className="loading">Loading...</div>;

  return (
    <div className="event-details-page">
      {/* Hero Section */}
      <div
        className="event-hero"
        style={{ backgroundImage: `url(${event.posterImage})` }}
      >
        <h1>{event.name}</h1>
      </div>

      {/* Event Details */}
      {/* Render details dynamically based on the event type */}

      {/* Booking CTA */}
      {showModal && (
        <BookingModal event={event} onClose={() => setShowModal(false)} />
      )}
    </div>
  );
};

export default EventDetailsPage;
